data_executive.each do |hash|
	puts hash[:city_name] 
	city = CityGovernmentAgency.find_by(city_name: hash[:city_name])
	city_branch = city.branches.find_by(branch_name: "Prefeitura")
	city_branch.website = hash[:website] if hash[:website].present? 
	city_branch.twitter = hash[:twitter] if hash[:twitter].present? 
	city_branch.email = hash[:email] if hash[:email].present? 
	city_branch.save!
end

data_legislative.each do |hash|
	puts hash[:city_name]
	city = CityGovernmentAgency.find_by(city_name: hash[:city_name])
	city_branch = city.branches.find_by(branch_name: "Câmara de Vereadores")
	city_branch.website = hash[:website] if hash[:website].present? 
	city_branch.twitter = hash[:twitter] if hash[:twitter].present? 
	city_branch.email = hash[:email] if hash[:email].present? 
	city_branch.save!
end


